	end
	
	if not (FileExist(LIB_PATH.."S1mpleOrbWalker.lua") and FileExist(SCRIPT_PATH.."S1Loader.lua")) and not FileExist(SCRIPT_PATH.."SidasAutoCarryReborn.lua") and not FileExist(SCRIPT_PATH.."SidasAutoCarry.lua") then
